import React, { useState } from 'react'
import './CSS/LoginSignup.css'

export default function LoginSignup() {
  const [state,setState]=useState("Login");
  const [formData,setFormData]=useState({
    username:"",
    password:"",
    email:""
  })
  const changeHandler=(e)=>{
    setFormData({...formData,[e.target.name]:e.target.value});  
  }
  const login = async () => {
    let dataObj;
    await fetch('http://localhost:4000/login', {
      method: 'POST',
      headers: {
        Accept:'application/form-data',
        'Content-Type':'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((resp) => resp.json())
      .then((data) => {dataObj=data});
      console.log(dataObj);
      if (dataObj.success) {
        localStorage.setItem('auth-token',dataObj.token);
        window.location.replace("/");
      }
      else
      {
        alert(dataObj.errors)
      }
  }

  const signup = async () => {
    let dataObj;
    await fetch('http://localhost:4000/signup', {
      method: 'POST',
      headers: {
        Accept:'application/form-data',
        'Content-Type':'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((resp) => resp.json())
      .then((data) => {dataObj=data});

      if (dataObj.success) {
        localStorage.setItem('auth-token',dataObj.token);
        window.location.replace("/");
      }
      else
      {
        alert(dataObj.errors)
      }
  }

  return (
    <div className='loginsignup'>
      <div className="loginsignup-container">
        <h1>{state}</h1>
        <div className="loginsignup-fields">
         {state==="Sign Up"?<input type="text" placeholder='your name' name="" id="" />:<></>} 
          <input type="email" placeholder='Your email' name="" id="" />
          <input type="password" placeholder='enter password' name="" id="" />

          <button onClick={()=>{state==="Login"?login():signup()}} >
            Continue
          </button>
          {state==="Sign Up"?<p className="loginsignup-login">
            Already have account? <span onClick={()=>{setState("Login")}} >Login Here</span>
          </p>: <p className="loginsignup-login">
            Create an account? <span onClick={()=>{setState("Sign Up")}} >Click here</span>
          </p>}
          <div className="loginsignup-agree">
            <input type="checkbox" name='' id='' />
            <p>By continuing, i agree to terms</p>
          </div>
        </div>
      </div>
    </div>
  )
}
